import { createUseStyles } from "react-jss";

const useStyles = createUseStyles({
    main: {
        display: "flex",
        paddingTop: "4rem",

    },
    contact: {
        display: "flex",

    },
    textarea:{
        height: "25px",
    },

    button:{
        backgroundColor: "#00bcd4",
        color: "white",
        border: "none",
        padding: "1rem",
        margin: "1rem",
        "&:hover": {
            backgroundColor: "#00acc1",
            color: "white",

    },
    },

    commentarea:{
        marginTop: "5rem",
    }

});

export default useStyles;
