import React, { useState, useEffect } from "react";
import axios from 'axios'
import { contact, } from '../../portfolio'
import './Contact.css'

const Contact = () => {

  const [dataUser, setDataUser] = useState({
		name: "",
		email: "",
		comment: ""
	})


  const [countComment, setCountComment] = useState()


  const [users, setUsers] = useState([])


  const getUsers = async () => {
      const response = await axios.get(
          "https://server-auliarahmanzulfi.herokuapp.com/users"
      )
      console.log(response.data)
      setCountComment(response.data.length)
      setUsers(response.data)
  }

const deleteUser = async (id) => {
  try {
    await axios.delete(
      `https://server-auliarahmanzulfi.herokuapp.com/users/${id}`
    )
    getUsers()
  } catch (err) {
    console.log(err)
  }
}


  const postComment = async (e) => {
      e.preventDefault()
      try{
    if (dataUser.name.length === 0) dataUser.name = "Anonymous"

          const response = await axios.post(
              "http://localhost:8000/api/komentars",
              dataUser
          )   
          console.log(response.data)
    window.location.reload()
      }catch(err){
          console.log(err)
      }
  }

  useEffect(() => {
      getUsers()
  }, [])

  return (

      <div className={contact.main}>
          <div className={contact.boxComment}>
             <h4>Leave a Comment</h4>
      <form onSubmit={postComment} className={contact.form}>
        <input
          className={contact.input}
          type="text"
          placeholder="Name (Optional)"
          value={dataUser.name}
          onChange={(e) => {
            setDataUser({ ...dataUser, name: e.target.value })
          }}
        />
        <input
          className={contact.input}
          type="email"
          placeholder="Email"
          value={dataUser.email}
          onChange={(e) => {
            setDataUser({ ...dataUser, email: e.target.value })
          }}
          required
        />
        <textarea
          className={contact.textarea}
          placeholder="Message"
          value={dataUser.comment}
          onChange={(e) => {
            setDataUser({
              ...dataUser,
              comment: e.target.value,
            })
          }}
          required
        />
        <button type="submit" className={contact.button}>
          Send
        </button>
      </form>
          </div>
   </div>
  )

  // )
}

export default Contact
